use Google\Cloud\ServiceBuilder;

$cloud = new ServiceBuilder();
$datastore = $cloud->datastore();

$key = $datastore->key('Person', 'Bob');
$entity = $datastore->entity($key, [
    'firstName' => 'Bob',
    'lastName' => 'Testguy'
]);

echo $entity['firstName']; // 'Bob'
$entity['location'] = 'Detroit, MI';
